using System.Collections.Generic;using System.Windows;using System.Windows.Controls;namespace Ejercicio13{    public partial class MainWindow : Window    {        public class Animal        {            public string Nombre { get; set; }            public string Imagen { get; set; }        }        public MainWindow()        {            InitializeComponent();            CargarAnimales();        }        private void CargarAnimales()        {            List<Animal> animales = new List<Animal>            {                new Animal { Nombre = "Perro", Imagen = "Resources/dog.png" },                new Animal { Nombre = "Gato", Imagen = "Resources/cat.png" },                new Animal { Nombre = "Pájaro", Imagen = "Resources/bird.png" }            };            AnimalComboBox.ItemsSource = animales;        }        private void AnimalComboBox_SelectionChanged(object sender, SelectionChangedEventArgs e)        {            if (AnimalComboBox.SelectedItem is Animal animalSeleccionado)            {                MessageBox.Show($"Has seleccionado: {animalSeleccionado.Nombre}");            }        }    }}